mapper.delete:
DELETE
FROM TF_DB_QUEUE A
WHERE A.APPLICATION_NAME = #{applicationName}
  AND A.PROFILES_ACTIVE = #{profilesActive}
  AND A.STATUS = 2

mapper.select:
SELECT *
FROM (
         SELECT ID
         FROM TF_DB_QUEUE A
         WHERE A.APPLICATION_NAME = #{applicationName}
           AND A.PROFILES_ACTIVE = #{profilesActive}
           AND A.QUEUE_NAME = #{queueName}
           AND A.STATUS = 1
         ORDER BY A.SEND_DATE ASC
     )
WHERE ROWNUM  <=  #{limit}
